import React, { useState, useEffect } from "react";
import { languageOptions } from "../constants/languageOptions";
import Dropdown from './Dropdown';
import { CaretDown } from './Icon';

const LanguagesDropdown = ({ handleLanguageChange, language }) => {
  const [currentLanguage, setCurrentLanguage] = useState(language);

  useEffect(() => {
    // Set the default language to be the first language in the list
    if (!currentLanguage && languageOptions.length > 0) {
      setCurrentLanguage(languageOptions[0].value);
    }
  }, [currentLanguage]);

  const handleChange = (newLanguage) => {
    const selectedLanguage = languageOptions.find(
      (lang) => lang.value === newLanguage
    );
    setCurrentLanguage(selectedLanguage);
    handleLanguageChange(selectedLanguage);
  };

  return (
    <div className="flex items-center">
      <Dropdown
        icon={<CaretDown />}
        altText="Language Actions"
        textColor="text-primary-primary500"
        placeholder={`Select Language`}
        options={languageOptions.map((lang) => ({
          label: lang.label,
          value: lang.value,
          key: lang.id,
        }))}
        onChange={handleChange}
        value={currentLanguage?.value}
        className="w-28"
      />
    </div>
  );
};

export default LanguagesDropdown;
