import React, { useState } from 'react';

function Dropbox({ label, icon, altText, bgColor = "bg-primary-primary500", textColor = "text-base-white", borderColor = "border-primary-primary500", options = [] }) {
  const [selectedOption, setSelectedOption] = useState(label);

  const handleChange = (event) => {
    const selected = event.target.value;
    setSelectedOption(selected);
    const selectedOptionObject = options.find(option => option.label === selected);
    if (selectedOptionObject && selectedOptionObject.onClick) {
      selectedOptionObject.onClick();
    }
  };

  return (
    <div className="relative inline-block w-full">
      <div className="flex gap-2 justify-center items-center px-3 py-2 cursor-pointer rounded-xl hover:shadow-lg">
        <select
          value={selectedOption}
          onChange={handleChange}
          className={`appearance-none bg-transparent ${bgColor} ${textColor} ${borderColor} border rounded-xl focus:outline-none px-3 py-2 w-full`}
          aria-label={altText}
        >
          <option disabled>{label}</option>
          {options.map((option, index) => (
            <option key={index} value={option.label}>
              {option.label}
            </option>
          ))}
        </select>
        <div className={`shrink-0 my-auto w-4 aspect-square`}>
          {icon}
        </div>
      </div>
    </div>
  );
}

export default Dropbox;
