import React, { useState, useEffect } from "react";
import monacoThemes from "monaco-themes/themes/themelist";
import { CaretDown } from './Icon'; // Assuming CaretDown is an icon component

const ThemeDropdown = ({ handleThemeChange, theme }) => {
  const [currentTheme, setCurrentTheme] = useState(theme);

  const handleChange = (event) => {
    const newTheme = event.target.value;
    setCurrentTheme(newTheme);
    handleThemeChange(newTheme);
  };

  useEffect(() => {
    // Set the default theme to be the first theme in the list if not selected yet
    if (!currentTheme && Object.keys(monacoThemes).length > 0) {
      setCurrentTheme(Object.keys(monacoThemes)[0]);
    }
  }, [currentTheme, monacoThemes]);

  return (
    <div className="flex flex-wrap gap-2 items-center">
      <span className="flex items-center">
        <CaretDown />
      </span>
      <select
        className="appearance-none bg-white border border-gray-300 rounded-md py-2 pl-3 pr-10 text-base leading-6 text-gray-700 focus:outline-none focus:border-blue-500 cursor-pointer bg-no-repeat bg-right"
        value={currentTheme}
        onChange={handleChange}
        aria-label="Theme Actions"
        style={{
          backgroundImage: 'url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\' viewBox=\'0 0 4 5\'><path fill=\'%23666\' d=\'M2 0L0 2h4L2 0z\'/></svg>")',
          backgroundSize: '0.65rem auto',
        }}
      >
        <option value="" disabled>Select Theme</option>
        {Object.entries(monacoThemes).map(([themeId, themeName]) => (
          <option key={themeId} value={themeId}>
            {themeName}
          </option>
        ))}
      </select>
    </div>
  );
};

export default ThemeDropdown;
