import React, { useEffect, useState, useRef } from "react";
import CodeEditorSection from "./components/CodeEditorSection";
import Input from "./components/Input";
import OutputDetails from "./components/OutputDetails";
import OutputWindow from "./components/OutputWindow";
import {
  Play,
  Stop,
  ShareFat,
  Download,
  UsersFour,
  CodeBlock,
  Sun,
  Moon,
} from "./components/Icon";
import ThemeDropdown from "./components/ThemeDropdown";
import Button from "./components/Button";
import { defineTheme } from "./libs/defineTheme";
import monacoThemes from "monaco-themes/themes/themelist";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import debounce from "lodash.debounce";
import LanguagesDropdown from "./components/LanguagesDropdown";
import { languageOptions } from "./constants/languageOptions";

function App() {
  const pythonDefault = `# Write your Python code here\nprint("Hello, Python!")`;
  const javaDefault = `// Write your Java code here\npublic class Main {\n  public static void main(String[] args) {\n    System.out.println("Hello, Java!");\n  }\n}`;
  const cDefault = `#include <stdio.h>\n\nint main() {\n  printf("Hello, C!");\n  return 0;\n}`;
  const cppDefault = `#include <iostream>\n\nint main() {\n  std::cout << "Hello, C++!" << std::endl;\n  return 0;\n}`;

  const [code, setCode] = useState(cDefault);
  const [customInput, setCustomInput] = useState("");
  const [outputDetails, setOutputDetails] = useState(null);
  const [processing, setProcessing] = useState(false);
  const [theme, setTheme] = useState(null);
  const [language, setLanguage] = useState(languageOptions[0]);
  const [darkMode, setDarkMode] = useState(false);

  const containerRef = useRef(null);

  useEffect(() => {
    const handleResize = debounce(() => {
      console.log("Resized!");
    }, 100);

    const resizeObserver = new ResizeObserver(() => {
      handleResize();
    });

    if (containerRef.current) {
      resizeObserver.observe(containerRef.current);
    }

    return () => {
      resizeObserver.disconnect();
      handleResize.cancel();
    };
  }, []);

  useEffect(() => {
    if (!theme && Object.keys(monacoThemes).length > 0) {
      const firstTheme = Object.keys(monacoThemes)[0];
      defineTheme(firstTheme).then((_) => setTheme(firstTheme));
    }
  }, [theme]);

  useEffect(() => {
    if (language) {
      switch (language.value) {
        case "py":
          setCode(pythonDefault);
          break;
        case "java":
          setCode(javaDefault);
          break;
        case "c":
          setCode(cDefault);
          break;
        case "cpp":
          setCode(cppDefault);
          break;
        default:
          setCode("");
      }
    }
  }, [language, pythonDefault, javaDefault, cDefault, cppDefault]);

  const getStatusDescription = (statusId) => {
    switch (statusId) {
      case 200:
        return "Compile successfully";
      case 400:
        return "Bad request";
      case 500:
        return "Server error";
      default:
        return "Unknown status";
    }
  };

  const handleCompile = async () => {
    setProcessing(true);
    try {
      const response = await fetch("https://api.codex.jaagrav.in", {
        method: "POST",
        headers: {
          "Content-Type": "application/x-www-form-urlencoded"
        },
        body: new URLSearchParams({
          code,
          language: language.value,
          input: customInput
        })
      });
      const data = await response.json();
      setOutputDetails(data);

      const statusMessage = getStatusDescription(data.status);
      toast(`Status: ${data.status} - ${statusMessage}`);
    } catch (error) {
      handleError(error);
    } finally {
      setProcessing(false);
    }
  };

  const handleThemeChange = (themeId) => {
    defineTheme(themeId).then((_) =>
      setTheme({ value: themeId, label: monacoThemes[themeId] })
    );
  };

  const handleLanguageChange = (newLanguage) => {
    setLanguage(newLanguage);
  };

  const onChange = (action, data) => {
    switch (action) {
      case "code": {
        setCode(data);
        break;
      }
      default: {
        console.warn("case not handled!", action, data);
      }
    }
  };

  const toggleDarkMode = () => {
    setDarkMode((prevMode) => !prevMode);
    document.documentElement.classList.toggle("dark");
  };

  const handleError = (error) => {
    console.error("Error:", error);
    toast.error("An error occurred while compiling the code: " + error.message);
  };

  return (
    <div ref={containerRef} className={`${darkMode ? "dark" : ""}`}>
      <ToastContainer
        position="top-right"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme={darkMode ? "dark" : "light"}
      />
      <div className="flex flex-col justify-center p-5 bg-white dark:bg-gray-800">
        <header className="flex flex-col w-full px-2 lg:px-4">
          <h1 className="text-2xl font-bold leading-snug text-base-black dark:text-base-white">
            Online code editor
          </h1>
          <nav className="flex flex-wrap justify-between mt-6 text-base gap-5">
            <div className="flex flex-wrap gap-2">
              <div className="flex flex-wrap gap-2">
                <Button
                  label={processing ? "Processing" : "Run"}
                  icon={<Play />}
                  altText="Run"
                  bgColor="bg-primary-primary500"
                  textColor="text-base-white"
                  borderColor="border-primary-primary500"
                  hoverBgColor="hover:bg-primary-primary900"
                  hoverTextColor="hover:text-base-white"
                  hoverBorderColor="hover:border-primary-primary600"
                  onClick={handleCompile}
                  disabled={!code}
                />
                <Button
                  label="Stop"
                  icon={<Stop />}
                  altText="Stop Command"
                  textColor="text-primary-primary500"
                />
                <Button
                  label="Formatter"
                  icon={<CodeBlock />}
                  altText="Formatter Command"
                  textColor="text-primary-primary500"
                />
              </div>
              <div className="shrink-0 w-px border border-solid h-[35px] border-zinc-200 dark:border-zinc-600" />
              <div className="flex flex-wrap gap-2">
                <Button
                  label="Share"
                  icon={<ShareFat />}
                  altText="Share Action"
                  textColor="text-primary-primary500"
                />
                <Button
                  label="Collaborate"
                  icon={<UsersFour />}
                  altText="Collaborate Action"
                  textColor="text-primary-primary500"
                />
                <Button
                  label="Download"
                  icon={<Download />}
                  altText="Download Action"
                  textColor="text-primary-primary500"
                />
              </div>
            </div>
            <div className="flex flex-wrap gap-5 justify-between items-center">
              <div className="relative group">
                {darkMode ? (
                  <button
                    onClick={toggleDarkMode}
                    className="flex items-center space-x-2 text-black dark:text-white"
                  >
                    <Sun />
                    <span className="absolute bottom-full mb-2 w-max px-2 py-1 text-xs text-white bg-black rounded opacity-0 group-hover:opacity-100">
                      Light mode
                    </span>
                  </button>
                ) : (
                  <button
                    onClick={toggleDarkMode}
                    className="flex items-center space-x-2 text-black dark:text-white"
                  >
                    <Moon />
                    <span className="absolute bottom-full mb-2 w-max px-2 py-1 text-xs text-white bg-black rounded opacity-0 group-hover:opacity-100">
                      Dark mode
                    </span>
                  </button>
                )}
              </div>
              <label className="text-base-black dark:text-base-white font-bold">Language</label>
              <LanguagesDropdown
                handleLanguageChange={handleLanguageChange}
                language={language}
                className="w-32"
              />
              <label className="text-base-black dark:text-base-white font-bold">Theme</label>
              <ThemeDropdown
                handleThemeChange={handleThemeChange}
                theme={theme}
                className="w-32"
              />
            </div>
          </nav>
        </header>
        <main className="mt-10 flex flex-col md:flex-row gap-5">
          <section className="flex flex-col w-full md:w-3/5">
            <CodeEditorSection
              code={code}
              onChange={onChange}
              language={language?.value}
              theme={theme}
              darkMode={darkMode}
            />
          </section>
          <section className="flex flex-col w-full md:w-2/5">
            <article className="flex flex-col grow self-stretch pb-6 text-2xl font-bold leading-7 text-black dark:text-white mt-10 md:mt-0">
              <div className="flex flex-col whitespace-nowrap">
                <OutputWindow outputDetails={outputDetails} />
              </div>
              <div className="flex flex-col mt-5">
                <h2>Input</h2>
                <div className="flex flex-col items-end">
                  <Input
                    customInput={customInput}
                    setCustomInput={setCustomInput}
                  />
                </div>
              </div>
              <div className="flex flex-col justify-end self-start mt-5 whitespace-nowrap">
                {outputDetails && (
                  <OutputDetails outputDetails={outputDetails} darkMode={darkMode} />
                )}
              </div>
            </article>
          </section>
        </main>
      </div>
    </div>
  );
}

export default App;
