import React from "react";

const Input = ({ customInput, setCustomInput }) => {
  const classNames = [
    "focus:outline-none",
    "w-full",
    "border-2",
    "border-primary-primary500",  // Matching primary border color
    "rounded-md",
    "shadow-[5px_5px_0px_0px_rgba(0,0,0)]",
    "px-4",
    "py-2",
    "hover:shadow-lg",
    "transition",
    "duration-200",
    "bg-white",
    "mt-2",
    "text-primary-primary500", // Matching primary text color
  ].join(" ");

  return (
    <textarea
      rows="5"
      value={customInput}
      onChange={(e) => setCustomInput(e.target.value)}
      placeholder="type input here"
      className={classNames}
    ></textarea>
  );
};

export default Input;
