// @ts-check

import React, { useState, useEffect, useRef } from "react";
import Editor, { useMonaco } from "@monaco-editor/react";

const CodeEditorSection = ({ onChange, language, code, theme }) => {
  const [value, setValue] = useState(code || "");
  const [editorTheme, setEditorTheme] = useState(theme ? theme.value : "vs-dark");
  const editorRef = useRef(null);
  const socket = useRef(null);
  const monaco = useRef(null)

  const handleEditorChange = (value) => {
    setValue(value);
    onChange("code", value);
    sendCodeToLsp(value);
  };

  useEffect(() => {
    setValue(code); // Update the value when code prop changes
  }, [code]);

  useEffect(() => {
    setEditorTheme(theme ? theme.value : "vs-dark");
  }, [theme]);

  useEffect(() => {
    if (socket.current) return // exit socket=
    // Ensure WebSocket connection is handled properly
    if (socket.current && socket.current.readyState === WebSocket.OPEN) {
      socket.current.close();
    }
    console.log('Connecting to WebSocket...');
    socket.current = new WebSocket('ws://localhost:8080/lsp');

    socket.current.onopen = () => {
      console.log('WebSocket connection opened');
    };

    socket.current.onmessage = (message) => {
      const data = JSON.parse(message.data);
      if (data.method === 'textDocument/completion') {
        const suggestions = data.result.items.map(item => ({
          label: item.label,
          kind: monaco.current.languages.CompletionItemKind.Function,
          documentation: item.documentation,
          insertText: item.insertText
        }));
        monaco.current.languages.registerCompletionItemProvider(language, {
          provideCompletionItems: () => ({
            suggestions: suggestions
          })
        });
      } else if (data.method === 'textDocument/hover') {
        const hoverData = data.result.contents.value;
        monaco.current.languages.registerHoverProvider(language, {
          provideHover: () => ({
            contents: [{ value: hoverData }]
          })
        });
      }
    };

    socket.current.onclose = (event) => {
      console.log('WebSocket connection closed:', event.reason);
      // Attempt to reconnect after a delay
      setTimeout(() => {
        if (socket.current.readyState !== WebSocket.OPEN) {
          console.log('Reconnecting to WebSocket...');
          socket.current = new WebSocket('ws://localhost:8080/lsp');
        }
      }, 1000);
    };

    socket.current.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    return () => {
      if (socket.current && socket.current.readyState === WebSocket.OPEN) {
        socket.current.close();
      }
    };
  }, []);

  useEffect(() => {
    if (monaco.current && editorRef.current) {
      monaco.current.editor.setModelLanguage(editorRef.current.getModel(), language);
    }
  }, [language, monaco]);

  const sendCodeToLsp = (code) => {
    if (editorRef.current && socket.current && socket.current.readyState === WebSocket.OPEN) {
      socket.current.send(JSON.stringify({
        jsonrpc: '2.0',
        id: 1,
        method: 'textDocument/didChange',
        params: {
          textDocument: { uri: 'file:///' + editorRef.current.getModel().uri.path },
          contentChanges: [{ text: code }]
        }
      }));
    }
  };

  return (
    <div className="overlay rounded-md overflow-hidden w-full h-full shadow-4xl">
      <Editor
        height="85vh"
        width={`100%`}
        language={language || "c"}
        value={value}
        theme={editorTheme}
        defaultValue={code}
        onChange={handleEditorChange}
        onMount={value => monaco.current = value}
        editorDidMount={(editor) => {
          editorRef.current = editor;
        }}
      />
    </div>
  );
};

export default CodeEditorSection;
