import React, { useState } from 'react';

function Dropdown({ label, icon, altText, bgColor = "bg-primary-primary500", textColor = "text-base-white", borderColor = "border-primary-primary500", options = [] }) {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="relative inline-block">
      <button
        className={`flex gap-2 justify-center px-3 py-2 ${bgColor} ${textColor} ${borderColor} border rounded-xl hover:bg-primary-primary500 hover:border-primary-primary600 hover:text-base-white hover:shadow-lg`}
        aria-label={altText}
        onClick={toggleDropdown}
      >
        <span className="font-bold">{label}</span>
        <div className={`shrink-0 my-auto w-4 aspect-square`}>
            {icon}
        </div>
      </button>
      {isOpen && (
        <div className="absolute left-0 mt-2 w-48 max-h-48 overflow-y-auto bg-base-white border border-gray-300 rounded-xl shadow-lg z-10">
          <ul className="py-1">
            {options.map((option, index) => (
              <li
                key={index}
                className="px-4 py-2 cursor-pointer text-primary-primary600 hover:bg-gray-200"
                onClick={() => {
                  setIsOpen(false);
                  option.onClick();
                }}
              >
                {option.label}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Dropdown;
