import React, { useEffect, useState, useRef } from "react";

import CodeEditorSection from "./components/CodeEditorSection";
import Input from "./components/Input";
import OutputDetails from "./components/OutputDetails";
import OutputWindow from "./components/OutputWindow";
import {
  Play,
  Stop,
  ShareFat,
  Download,
  UsersFour,
  CodeBlock,
  Sun,
  Moon,
} from "./components/Icon";
import ThemeDropdown from "./components/ThemeDropdown";
import Button from "./components/Button";
import Divider from "./components/Divider";
import { defineTheme } from "./libs/defineTheme";
import monacoThemes from "monaco-themes/themes/themelist";

import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import debounce from "lodash.debounce";
import LanguagesDropdown from "./components/LanguagesDropdown";

function App() {
  const pythonDefault = `# Write your Python code here\nprint("Hello, Python!")`;
  const javaDefault = `// Write your Java code here\npublic class Main {\n  public static void main(String[] args) {\n    System.out.println("Hello, Java!");\n  }\n}`;
  const cDefault = `#include <stdio.h>\n\nint main() {\n  printf("Hello, C!");\n  return 0;\n}`;
  const cppDefault = `#include <iostream>\n\nint main() {\n  std::cout << "Hello, C++!" << std::endl;\n  return 0;\n}`;

  const options = [
    { label: "Option 1", onClick: () => alert("Option 1 clicked") },
    { label: "Option 2", onClick: () => alert("Option 2 clicked") },
    { label: "Option 3", onClick: () => alert("Option 3 clicked") },
  ];

  const [code, setCode] = useState(cDefault);
  const [customInput, setCustomInput] = useState("");
  const [outputDetails, setOutputDetails] = useState(null);
  const [processing, setProcessing] = useState(null);
  const [theme, setTheme] = useState("cobalt");
  const [language, setLanguage] = useState({
    label: "C (GCC 9.2.0)",
    value: "c",
  });
  const [darkMode, setDarkMode] = useState(false);

  const containerRef = useRef(null);

  useEffect(() => {
    const handleResize = debounce(() => {
      console.log("Resized!");
    }, 100);

    const resizeObserver = new ResizeObserver(() => {
      handleResize();
    });

    if (containerRef.current) {
      resizeObserver.observe(containerRef.current);
    }

    return () => {
      resizeObserver.disconnect();
      handleResize.cancel();
    };
  }, []);

  useEffect(() => {
    switch (language.value) {
      case "python":
        setCode(pythonDefault);
        break;
      case "java":
        setCode(javaDefault);
        break;
      case "c":
        setCode(cDefault);
        break;
      case "cpp":
        setCode(cppDefault);
        break;
      default:
        setCode("");
    }
  }, [language]);

  const handleCompile = () => {
    // We will come to the implementation later in the code
  };

  // const checkStatus = async (token) => {
  //   // We will come to the implementation later in the code
  // };

  // Modify the handleThemeChange function to update the theme state
  function handleThemeChange(themeId) {
    defineTheme(themeId).then((_) =>
      setTheme({ value: themeId, label: monacoThemes[themeId] })
    );
  }

  const handleLanguageChange = (newLanguage) => {
    setLanguage(newLanguage);
  };

  const onChange = (action, data) => {
    switch (action) {
      case "code": {
        setCode(data);
        break;
      }
      default: {
        console.warn("case not handled!", action, data);
      }
    }
  };

  // const showSuccessToast = (msg) => {
  //   toast.success(msg || `Compiled Successfully!`, {
  //     position: "top-right",
  //     autoClose: 1000,
  //     hideProgressBar: false,
  //     closeOnClick: true,
  //     pauseOnHover: true,
  //     draggable: true,
  //     progress: undefined,
  //   });
  // };
  // const showErrorToast = (msg) => {
  //   toast.error(msg || `Something went wrong! Please try again.`, {
  //     position: "top-right",
  //     autoClose: 1000,
  //     hideProgressBar: false,
  //     closeOnClick: true,
  //     pauseOnHover: true,
  //     draggable: true,
  //     progress: undefined,
  //   });
  // };

  return (
    <div ref={containerRef}>
      <ToastContainer
        position="top-right"
        autoClose={2000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
      <div className="flex flex-col justify-center p-5 bg-white max-md:px-5">
        <header className="flex flex-col w-full px-2 lg:px-4">
          <h1 className="text-2xl font-bold leading-snug text-base-black">
            Online code editor
          </h1>
          <nav className="flex flex-wrap justify-between mt-6 text-base gap-5">
            <div className="flex flex-wrap gap-2">
              <div className="flex flex-wrap gap-2">
                <Button
                  label="Run"
                  icon={<Play />}
                  altText="Run"
                  bgColor="bg-primary-primary500"
                  textColor="text-base-white"
                  borderColor="border-primary-primary500"
                  hoverBgColor="hover:bg-primary-primary900"
                  hoverTextColor="hover:text-base-white"
                  hoverBorderColor="hover:border-primary-primary600"
                />
                <Button
                  label="Stop"
                  icon={<Stop />}
                  altText="Stop Command"
                  bgColor="border"
                  textColor="text-primary-primary500"
                />
                <Button
                  label="Formatter"
                  icon={<CodeBlock />}
                  altText="Formatter Command"
                  bgColor="border"
                  textColor="text-primary-primary500"
                />
              </div>
              <Divider />
              <div className="flex flex-wrap gap-2">
                <Button
                  label="Share"
                  icon={<ShareFat />}
                  altText="Share Action"
                  bgColor="border"
                  textColor="text-primary-primary500"
                />
                <Button
                  label="Collaborate"
                  icon={<UsersFour />}
                  altText="Collaborate Action"
                  bgColor="border"
                  textColor="text-primary-primary500"
                />
                <Button
                  label="Download"
                  icon={<Download />}
                  altText="Download Action"
                  bgColor="border"
                  textColor="text-primary-primary500"
                />
              </div>
            </div>
            <div className="flex flex-wrap gap-5 justify-between">
              <LanguagesDropdown
                handleLanguageChange={handleLanguageChange}
                language={language}
              />
              <ThemeDropdown
                handleThemeChange={handleThemeChange}
                theme={theme}
              />
            </div>
          </nav>
        </header>
        <main className="mt-10 max-md:max-w-full">
          <div className="flex gap-5 max-md:flex-col max-md:gap-0">
            <section className="flex flex-col w-3/5 max-md:ml-0 max-md:w-full">
              <CodeEditorSection
                code={code}
                onChange={onChange}
                language={language?.value}
                theme={theme}
              />
            </section>
            <section className="flex flex-col ml-5 w-2/5 max-md:ml-0 max-md:w-full">
              <article className="flex flex-col grow self-stretch pb-6 text-2xl font-bold leading-7 text-black max-md:mt-10 max-md:max-w-full">
                <div className="flex flex-col whitespace-nowrap max-md:max-w-full">
                  <OutputWindow outputDetails={outputDetails} />
                </div>
                <div className="flex flex-col mt-5 max-md:max-w-full">
                  <h2 className="max-md:max-w-full">Input</h2>
                  <div className="flex flex-col items-end">
                    <Input
                      customInput={customInput}
                      setCustomInput={setCustomInput}
                    />
                    {/* <button
                      onClick={handleCompile}
                      disabled={!code}
                    >
                      {processing ? "Processing..." : "Compile and Execute"}
                    </button> */}
                  </div>
                </div>
                <div className="flex flex-col justify-end self-start mt-5 whitespace-nowrap">
                  {outputDetails && (
                    <OutputDetails outputDetails={outputDetails} />
                  )}
                </div>
              </article>
            </section>
          </div>
        </main>
      </div>
    </div>
  );
}

export default App;
