import React, { useEffect, useState, useRef } from "react";
import Button from './Button';
import Divider from './Divider';
import Dropbox from './Dropdown';
import { Play, Stop, ShareFat, Download, UsersFour, CodeBlock, CaretDown } from './Icon';
import '../index.css';
import ThemeDropdown from './ThemeDropdown';
import { defineTheme } from "../libs/defineTheme";


function Header() {
  const [theme, setTheme] = useState("cobalt");


  const options = [
    { label: 'Option 1', onClick: () => alert('Option 1 clicked') },
    { label: 'Option 2', onClick: () => alert('Option 2 clicked') },
    { label: 'Option 3', onClick: () => alert('Option 3 clicked') },
  ];

  function handleThemeChange(th) {
    const theme = th;
    console.log("theme...", theme);

    if (["light", "vs-dark"].includes(theme.value)) {
      setTheme(theme);
    } else {
      defineTheme(theme.value).then((_) => setTheme(theme));
    }
  }
  useEffect(() => {
    defineTheme("oceanic-next").then((_) =>
      setTheme({ value: "oceanic-next", label: "Oceanic Next" })
    );
  }, []);



  return (
    <header className="flex flex-col w-full px-2 lg:px-4">
      <h1 className="text-2xl font-bold leading-snug text-base-black">
        Online code editor
      </h1>
      <nav className="flex flex-wrap justify-between mt-6 text-base gap-5">
        <div className="flex flex-wrap gap-2">
          <div className="flex flex-wrap gap-2">
            <Button
              label="Run"
              icon={<Play />}
              altText="Run"
              bgColor="bg-primary-primary500"
              textColor="text-base-white"
              borderColor="border-primary-primary500"
              hoverBgColor="hover:bg-primary-primary900"
              hoverTextColor="hover:text-base-white"
              hoverBorderColor="hover:border-primary-primary600"
            />
            <Button
              label="Stop"
              icon={<Stop />}
              altText="Stop Command"
              bgColor="border"
              textColor="text-primary-primary500"
            />
            <Button
              label="Formatter"
              icon={<CodeBlock />}
              altText="Formatter Command"
              bgColor="border"
              textColor="text-primary-primary500"
            />
          </div>
          <Divider />
          <div className="flex flex-wrap gap-2">
            <Button
              label="Share"
              icon={<ShareFat />}
              altText="Share Action"
              bgColor="border"
              textColor="text-primary-primary500"
            />
            <Button
              label="Collaborate"
              icon={<UsersFour />}
              altText="Collaborate Action"
              bgColor="border"
              textColor="text-primary-primary500"
            />
            <Button
              label="Download"
              icon={<Download />}
              altText="Download Action"
              bgColor="border"
              textColor="text-primary-primary500"
            />
          </div>
        </div>
        <div className="flex flex-wrap gap-5 justify-between">
          <div className="flex flex-wrap gap-2 items-center">
            <label className="text-black font-bold">Language</label>
            <Dropbox
              label="C"
              icon={<CaretDown />}
              altText="Language Actions"
              bgColor="border"
              textColor="text-primary-primary500"
              options={options}
            />
          </div>
          {/* <div className="flex flex-wrap gap-2 items-center">
            <label className="text-black font-bold">Theme</label>
            <Dropbox
              label="Light"
              icon={<CaretDown />}
              altText="Theme Actions"
              bgColor="border"
              textColor="text-primary-primary500"
              options={options}
            />
          </div> */}
          <ThemeDropdown handleThemeChange={handleThemeChange} theme={theme}/>
        </div>
      </nav>
    </header>
  );
}

export default Header;
